import {useState} from 'react'
import styled from 'styled-components'


const StyledInput = styled.input`
       width: 130px;
  box-sizing: border-box;
  border: 2px solid #ccc;
  border-radius: 4px;
  font-size: 16px;
     transition: width 0.4s ease-in-out;

     &:focus{
     width: 100%;
     }
`

const StyledContainer = styled.div`
      margin-top: 40vh;
      margin-bottom: 1rem;
`

const SearchBox = ({handleClick}) =>{

    const [value, setValue] = useState('')
    return <StyledContainer>
        <StyledInput type="text" placeholder="Location" onChange={e => setValue(e.target.value)} />
        <button onClick={()=> handleClick(value)}><img width='10' src="https://img.icons8.com/android/24/000000/search.png"alt=""/></button>
    </StyledContainer>
}

export default SearchBox;
