import axios from 'axios'


const getLocation  = (location) =>{
    const baseURL = 'https://rickandmortyapi.com/api/location/'
    if(location){
        return axios(`${baseURL}${location}`,{
            method: 'GET'
        })
    }
    else{
        return null
    }
}

export default getLocation
