import LocationInfo from "./LocationInfo";
import {useEffect, useState} from 'react';
import getLocation from "../../services/getLocation";
import axios from "axios";
import ResidentContainer from "../resident/ResidentContainer";


const LocationContainer = () =>{

    const [name, setName] = useState('')
    const [type, setType] = useState('')
    const [dimension, setDimension] = useState('')
    const [amount, setAmount] = useState('')
    const [locations, setLocations] = useState([])
    const [location, setLocation] = useState('')

    useEffect(()=>{
        axios('https://rickandmortyapi.com/api/location', {method: 'GET'}).then(res => {
            setLocations(res.data.results)
        })
    }, [])

    useEffect(() => {
        setLocation(locations[Math.floor(Math.random() * locations.length)])

    }, [locations])

    useEffect(() => {
        if (location){
            setName(location.name)
            setType(location.type)
            setDimension(location.dimension)
            console.log(location)
            setAmount(location.residents.length)
        }
        },
        [location])

    const list = location.map(((vale, index) => {
        console.log('esto es location', location)
        return <ResidentContainer url={vale} />
    }));

    return (
        <div>
            <LocationInfo name={name} type={type} amount={amount} dimension={dimension} />
            {
                list
            }
        </div>
      )
}
export default LocationContainer;
