import LocationInfo from "./LocationInfo";
import {useEffect, useState} from 'react';
import getLocation from "../../services/getLocation";
import axios from "axios";


const LocationContainer = () =>{

    const [name, setName] = useState('')
    const [type, setType] = useState('')
    const [dimension, setDimension] = useState('')
    const [amount, setAmount] = useState('')
    const [locations, setLocations] = useState([])
    const [location, setLocation] = useState('')

    useEffect(()=>{
        axios('https://rickandmortyapi.com/api/location', {method: 'GET'}).then(res => setLocations(res.results))
        setLocation(locations[Math.floor(Math.random(locations.length))])
    }, [])

    useEffect(() => {
        getLocation(location)
            .then((response) =>{
                setName(response.data.name)
                setDimension(response.data.dimension)
                setType(response.data.type)
                setAmount(response.data.residents.length)
            })

    }, [locations])

    return (
        <div>
            <LocationInfo name={name} type={type} amount={amount} dimension={dimension} /> 
        </div>
      )
}
export default LocationContainer;
